	public ArrayList<ObjViewUserGeneral> loadGeneralPerfilInfo(String idUpdate,
			Long typeUpdate, Long type, Long idUser, Long limit, Long multiplier)
			throws Exception {
		Session session = SessionHibernate.getSession();
		Transaction tx = session.beginTransaction();

		try {
			PerfilDAO dao = new PerfilDAO();
			ArrayList<ObjViewUserGeneral> objList  = dao.loadGeneralPerfilInfo(idUpdate, typeUpdate, type,
					idUser, limit, multiplier);
			for(ObjViewUserGeneral obj : objList  ){
				verifyPhotoInServer(obj.getPhoto());
			}
			return objList;
			

		} catch (Exception e) {
			tx.rollback();
			e.printStackTrace();
			throw e;
		} finally {
			session.close();
		}

	}

	public void followUser(Long myId, Long idUser) {
		Session session = SessionHibernate.getSession();
		Transaction tx = session.beginTransaction();

		try {
			Friend friend = new Friend();
			friend.setIdFollowing(myId);
			friend.setIdFollowed(idUser);

			session.save(friend);

			tx.commit();

		} catch (Exception e) {
			tx.rollback();
			e.printStackTrace();

		} finally {
			session.close();
		}

	}

	public void unfollowUser(Long myId, Long idUser) {
		Session session = SessionHibernate.getSession();
		Transaction tx = session.beginTransaction();

		try {
			UserDAO dao = new UserDAO();
			ArrayList<Friend> frienList = dao.findFriendList(myId, idUser);
			for (Friend f : frienList) {
				session.delete(f);
			}

			tx.commit();

		} catch (Exception e) {
			tx.rollback();
			e.printStackTrace();

		} finally {
			session.close();
		}

	}

	public String savePhotoToDisk(Long idUser, Long idPhoto, String photo)
			throws IOException {
		try {

			String name = "p" + idUser + "-" + idPhoto + ".txt";
			File file = new File("/SocialFiles/photo/" + name);

			// if file doesnt exists, then create it
			if (!file.exists()) {

				file.createNewFile();
			}

			FileWriter fw = new FileWriter(file.getAbsoluteFile());
			BufferedWriter bw = new BufferedWriter(fw);
			bw.write(photo);
			bw.close();

			return file.getAbsolutePath();
		} catch (IOException e) {
			e.printStackTrace();
			throw e;
		}

	}

	public String savePhotoToDiskOriginal(Long idUser, Long idPhoto,
			String photo) throws IOException {
		try {
			// "data:image/jpg;base64,";
			photo = photo.substring(22, photo.length());

			BufferedImage bufferedImage = ImageUtil.decodeToImage(photo);
			if (bufferedImage == null) {
				System.out.println("LA IMAGEN ERA NULLL");
			}
			System.out.println("CONTINUE BIEN");
			String name = "p" + idUser + "-" + idPhoto + ".jpg";
			File file = new File("/SocialFiles/photo/" + name);

			ImageIO.write(bufferedImage, "jpg", file);

			/*
			 * FileWriter fw = new FileWriter(file.getAbsoluteFile());
			 * BufferedWriter bw = new BufferedWriter(fw); bw.write(photo);
			 * bw.close();
			 */

			return "../photos/"+name;
		} catch (IOException e) {
			e.printStackTrace();
			throw e;
		}

	}

	public String saveVideoToDisk(Long idUser, Long idVideo,
			InputStream inputstream, String extension) throws Exception {
		try {
			// extension including point
			String name = "v" + idUser + "-" + idVideo + extension;

			File file = new File("/SocialFiles/videos/" + name);

			// if file doesnt exists, then create it
			if (!file.exists()) {

				file.createNewFile();
			}

			// ruta del archivo salida
			FileOutputStream outputStream = new FileOutputStream(file);
			byte[] buf = new byte[1024];// Actualizado me olvide del 1024
			int len;
			while ((len = inputstream.read(buf)) > 0) {
				outputStream.write(buf, 0, len);
			}

			inputstream.reset();
			uploadVideoToServer(idUser, idVideo, inputstream, extension);

			outputStream.close();
			inputstream.close();

			System.out.println("Se realizo la conversion con exito");

			String absoulutePath = file.getAbsolutePath();

			file = null;

			return name;
		} catch (Exception e) {
			e.printStackTrace();
			throw e;
		}

	}

