	public ArrayList<String> searchHashtag(String postText) {
		try {

			ArrayList<String> hashtagList = new ArrayList<String>();
			boolean searching = false;
			String hasht = new String();
			for (int i = 0; i < postText.length(); i++) {
				String s = postText.substring(i, i + 1);
				if (s.equals("#") && !searching) {
					searching = true;
					hasht = new String();
					continue;
				}
				if (searching) {
					if (s.equals(" ")) {

						if (!hasht.isEmpty()) {

							hashtagList.add(hasht);
						}
						searching = false;
						hasht = new String();
						continue;
					}

					hasht = hasht + s;
				}
			}
			if (searching && !hasht.isEmpty()) {
				hashtagList.add(hasht);
			}

			return hashtagList;

		} catch (Exception e) {
			e.printStackTrace();
			return null;
		}

	}

